package repasoFeb.Bloque3Wrappers;

public class Ej1LimitsPrimitives {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Limite minimo de Byte: "+Byte.MIN_VALUE);
		System.out.println("Limite maximo de Byte: "+Byte.MAX_VALUE);
		
		System.out.println("Limite minimo de Integer: "+Integer.MIN_VALUE);
		System.out.println("Limite maximo de Integer: "+Integer.MAX_VALUE);
		
		System.out.println("Limite minimo de Short: "+Short.MIN_VALUE);
		System.out.println("Limite maximo de Short: "+Short.MAX_VALUE);
		
		System.out.println("Limite minimo de Long: "+Long.MIN_VALUE);
		System.out.println("Limite maximo de Long: "+Long.MAX_VALUE);
		
		System.out.println("Limite minimo de Float: "+Float.MIN_VALUE);
		System.out.println("Limite maximo de Float: "+Float.MAX_VALUE);
		
		System.out.println("Limite minimo de Double: "+Double.MIN_VALUE);
		System.out.println("Limite maximo de Double: "+Double.MAX_VALUE);
		
	}

}
